body.script:
var baseUrl = "http://localhost:81";

axios.interceptors.response.use(function(response) {
    return response;
},function (error) {
    vue.$message({
        message: '系统错误',
        type: 'error'
    });
});

var vue = new Vue({
    el:"#app",
    data:{
        dialogVisible:false,
        tableInfo:{},
        rules:{
            // 字段名称必须和请求参数名称相同
            // 字段名称:[{校验规则对象},{校验规则对象}]
            // required : 必填项
            // message : 提示信息
            // trigger : 触发方式 , blur : 失去焦点 change : 内容改变
            panme:[
                { required:true, message: '请输入游戏名称', trigger: 'blur' },
                { min: 5, max: 15, message: '长度在 5 到 15 个字符', trigger: 'blur' }
            ]
        },
        form:{
            page:1,
            pageSize:2
        },
        agents:[],
        types:[],
        selections:[]
    },
    // 在页面初始化完成的时候调用
    created:function () {
        this.find();
        this.load();
    },
    methods:{
        // 关闭dialog之前执行
        handleClose:function (done) {
            this.$confirm("确认关闭吗?","提示")
                .then(function() { // 点击确定执行
                    // 恢复form
                    vue.form = {
                        page:1,
                        pageSize:2
                    };
                    done(); // 关闭dialog
                })
        },
        // 修改
        edit:function (info) {
          this.form = info.row;
          this.dialogVisible = true;
        },
        del:function (info) {
            this.$confirm("确认删除吗?","提示")
                .then(function() {
                    axios.delete(baseUrl + "/${lowerName}/" + info.row.id)
                        .then(function (response) {
                            vue.$message.success("删除成功");
                            // 刷新list
                            vue.find();
                        })
                })
        },
        // 批量删除
        dels:function () {
            this.$confirm("确认删除吗?","提示")
                .then(function() {
                    // 提取id数据
                    var arr = [];
                    for (var i = 0; i < vue.selections.length; i++) {
                        var obj = vue.selections[i];
                        arr.push(obj.id);
                    }
                    axios.delete(baseUrl + "/${lowerName}/" +arr.join())
                        .then(function (response) {
                            vue.$message.success("删除成功");
                            // 刷新list
                            vue.find();
                        })
                })

        },
        // 获取选中的行
        handleSelectionChange:function (val) {
          this.selections = val;
        },
        // 保存数据
        save:function (formName) {
            this.$refs[formName].validate(function (valid) { // valid : 验证结果
                if(valid) {

                    axios.post(baseUrl + "/${lowerName}",Qs.stringify(vue.form))
                        .then(function (response) {
                            vue.$message({
                                message: '操作成功',
                                type: 'success'
                            });
                            // 关闭dialog
                            vue.dialogVisible = false;
                            // 清空form
                            vue.form={
                                page:1,
                                pageSize:2
                            }
                            // 刷新table
                            vue.find();
                        });
                }
            });

        },
        // 清空查询数据
        clear:function () {
            this.form.pname = null;
            this.form.agentId = null;
            this.form.typeId = null;
        },
        load:function () {
            // 接收2个数据
           // axios.post(baseUrl + "/player/load")
               // .then(function (response) {
               //     vue.agents = response.data.agents;
               //     vue.types = response.data.types;
               // });
        },
        handleSizeChange:function (val) {
            this.form.pageSize = val;
            this.find();
        },
        handleCurrentChange:function (val) {
            this.form.page = val;
            this.find();
        },
        find:function () {
            axios.post(baseUrl + "/${lowerName}/list",Qs.stringify(this.form))
                .then(function (response) {
                    // this : window 对象
                    vue.tableInfo = response.data;
                });
        }
    }
});
